                image: imagem,
                categoria: categoria,
                descricao: descricao,

                endereco: {
                    cep: cep,
                    logradouro: logradouro,
                    numero: numero,
                    bairro: bairro,
                    complemento: complemento,
                    cidade: cidade,
                    estado: estado
                }
            }
        } 

        axios(config).then(response => {
            console.log(response.data)
            navigate("/")
        })*/

    } 




    return (

       <div className="loc-form-container">
            <img className="loc-movie-card-image" src={imovel?.image} alt={imovel?.title} />
            <div className="loc-card-bottom-container">
                <h2>{imovel?.categoria}</h2>
                <h3>{imovel?.title}</h3>
                <form className="loc-form" onSubmit={handleSubmit} >
                    <div className="form-group loc-form-group">
                        {imovel.endereco.logradouro}, {imovel.endereco.numero} - {imovel.endereco.bairro} {imovel.endereco.cidade}-{imovel.endereco.estado}
                    </div>

                    <div className="loc-form-btn-container">
                        <button type="submit" className=" btn loc-btn">Alterar dados</button>
                    </div>
                </form >
                <Link to="/" style={{ textDecoration: "none" }}  >
                    <button className="btn  loc-btn mt-3">Excluir imóvel</button>

                </Link>
                <Link to="/" style={{ textDecoration: "none" }}  >
                    <button className="btn  loc-btn mt-3">Cancelar</button>

                </Link>
            </div >
        </div > 



    )
}
