import * as React from 'react'
import { Link, useNavigate } from 'react-router-dom';
import './form.css'
import { Imovel } from '../types/imovel'
import axios, { AxiosRequestConfig } from 'axios';
import { BASE_URL } from '../utils/requests';
import Cards from './cards';


 type Props = {
    imovel: Imovel

} 
export default function FormDetalhes( {imovel} : Props) {

    const navigate = useNavigate();

    const [mostraImovel, setImovel] = React.useState<Imovel>()

    React.useEffect(() => {
        axios.get(`${BASE_URL}/detalhes/${imovel.title}`)
            .then(response => {
                setImovel(response.data)
            })
    }, [imovel.title]) 

    const handleSubmit = (event: React.FormEvent<HTMLFormElement>) => {
        event.preventDefault();





        const config: AxiosRequestConfig = {
            baseURL: BASE_URL,
            method: 'GET',
            url: '/imovel',
            data: {
                title: titulo,
                image: imagem,
                categoria: categoria,
                descricao: descricao,

                endereco: {
                    cep: cep,
                    logradouro: logradouro,
                    numero: numero,
                    bairro: bairro,
                    complemento: complemento,
                    cidade: cidade,
                    estado: estado
                }
            }
        }

        axios(config).then(response => {
            console.log(response.data)
            navigate("/")
        })

    } 




    return (

       <div className="loc-form-container">
            <img className="loc-movie-card-image" src={imovel?.image} alt={imovel?.title} />
            <div className="loc-card-bottom-container">
                <h2>{imovel?.categoria}</h2>
                <h3>{imovel?.title}</h3>
                <form className="loc-form" onSubmit={handleSubmit} >
                    <div className="form-group loc-form-group">
                        {imovel.endereco.logradouro}, {imovel.endereco.numero} - {imovel.endereco.bairro} {imovel.endereco.cidade}-{imovel.endereco.estado}
                    </div>

                    <div className="loc-form-btn-container">
                        <button type="submit" className=" btn loc-btn">Alterar dados</button>
                    </div>
                </form >
                <Link to="/" style={{ textDecoration: "none" }}  >
                    <button className="btn  loc-btn mt-3">Excluir imóvel</button>

                </Link>
                <Link to="/" style={{ textDecoration: "none" }}  >
                    <button className="btn  loc-btn mt-3">Cancelar</button>

                </Link>
            </div >
        </div > 



    )
}
